import React from "react";

let themes = {
    dark:{
        backgroundColor: "#E54980",
        color: "#EFF2F6",
        textDecoration: none,
        fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
        
    },
    light: {
        backgroundColor: "#E6EDED",
        color: black,
        textDecoration: none,
        fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
    }
}

const ThemesContext = React.createContext(themes);

export {
        ThemesContext,
        themes};
