import ItemAdder from "../ItemAdder/ItemAdder";
import Header from "../Header/Header";
import TodosList from '../ToDoList/ToDoList'
import '../App/App.css'
import { ThemesContext } from "../context";


const App = () => {

    let theme = useContext(ThemesContext);

    
  
    return (  
        <div className="app"
        style = {checked ? theme.light.AppComponentTheme : theme.dark.AppComponentTheme}>
              <FormControlLabel 
        control={<Switch
        onChange={handleSwitchCheck}
                                    />} 
        label={checked ? "Light theme" : "Dark Theme" }/>
            
            <Header />
            <ItemAdder />
            <TodosList />
        </div>

    );
};

  

export default App;
