import ItemAdder from "../ItemAdder/ItemAdder";
import Header from "../Header/Header";
import TodosList from '../ToDoList/ToDoList'
import '../App/App.css'
import { ThemesContext } from "../context";
import { useContext, useState } from "react";
import Switch from '@mui/material/Switch';
import FormControlLabel from '@mui/material/FormControlLabel';

const App = () => {

    let theme = useContext(ThemesContext);
    let [checked, setChecked] = useState(true);
    let handleSwitchCheck = (e) => {
    setChecked(!e.target.checked)
    }
    return (  
        <div className="app"
        style = {checked ? theme.Light : theme.dark}>
              <FormControlLabel 
    control={<Switch
    onChange={handleSwitchCheck}
      />} 
    label={checked ? "Light theme" : "Dark Theme" } 
     />
            <Header />
            <ItemAdder />
            <TodosList />
        </div>

    );
};

  

export default App;
